function msgError() {
return '<span class="no-posts">Error: No Results Found.</span>';
}
function beforeLoader() {
return '<div class="loader"/>';
}
function getFeedUrl(action, label, type) {
var destination_wrapper = "";
switch(type) {
case "recent":
destination_wrapper = "/feeds/posts/default?alt=json&max-results=" + label;
break;
case "comments":
if (action == "list") {
destination_wrapper = "/feeds/comments/default?alt=json&max-results=" + label;
} else {
destination_wrapper = "/feeds/posts/default/-/" + type + "?alt=json&max-results=" + label;
}
break;
default:
destination_wrapper = "/feeds/posts/default/-/" + type + "?alt=json&max-results=" + label;
break;
}
return destination_wrapper;
}
function getPostLink(users, name) {
var i = 0;
for (; i < users[name].link.length; i++) {
if (users[name].link[i].rel == "alternate") {
var entityUrl = users[name].link[i].href;
break;
}
}
return entityUrl;
}
function getPostTitle(a, i) {
var value = a[i].title["$t"];
return value;
}
function getPostDate(res, i) {
var displayName = res[i].published["$t"];
var baseName = displayName.substring(0, 4);
var total_pageviews_raw = displayName.substring(5, 7);
var _0xd222x28 = displayName.substring(8, 10);
var middlePathName = monthFormat[parseInt(total_pageviews_raw, 10) - 1] + " " + _0xd222x28 + ", " + baseName;
var _0xd222x2a = '<span class="post-date"><time class="published" datetime="' + displayName + '">' + middlePathName + "</time></span>";
return _0xd222x2a;
}
function getPostImage(a, i) {
var value = a[i].content["$t"];
var oldCondition = $("<div>").html(value);
if ("media$thumbnail" in a[i]) {
var filePath = a[i]["media$thumbnail"].url;
var width = filePath.replace("/s72-c", "/w200-h130-p-k-no-nu");
var whatToScale = filePath.replace("/s72-c", "/w170-h120-p-k-no-nu");
var res = filePath.replace("/s72-c", "/w90-h65-p-k-no-nu");
if (filePath.match("img.youtube.com")) {
width = filePath.replace("/default.", "/mqdefault.");
whatToScale = width;
res = filePath;
}
} else {
if (value.indexOf("<img") > -1) {
width = oldCondition.find("img:first").attr("src");
whatToScale = width;
res = width;
} else {
width = noThumbnail.replace("/w300", "/w200-h130-p-k-no-nu");
whatToScale = noThumbnail.replace("/w300", "/w170-h120-p-k-no-nu");
res = noThumbnail.replace("/w300", "/w90-h65-p-k-no-nu");
}
}
var Aerial = '<span class="post-thumb" data-image="' + width + '"/>';
var AerialWithLabels = '<span class="post-thumb" data-image="' + whatToScale + '"/>';
var Road = '<span class="post-thumb" data-image="' + res + '"/>';
var VALID_IMAGERY_SETS = [Aerial, AerialWithLabels, Road];
return VALID_IMAGERY_SETS;
}
function getAjax(table, name, item, id) {
switch(name) {
case "msimple":
case "list":
case "related":
if (id == undefined) {
id = "geterror.x5j7a";
}
var task = getFeedUrl(name, item, id);
$.ajax({
url : task,
type : "GET",
dataType : "json",
beforeSend : function success(retu_data) {
switch(name) {
case "related":
table.html(beforeLoader()).parent().addClass("show-ify");
break;
case "list":
table.html(beforeLoader());
break;
}
},
success : function getList(data) {
var count_int = "";
switch(name) {
case "msimple":
count_int = '<ul class="mega-widget">';
break;
case "list":
count_int = '<div class="custom-widget">';
break;
case "related":
count_int = '<div class="related-posts">';
break;
}
var src = data.feed.entry;
if (src != undefined) {
var i = 0;
var post = src;
for (; i < post.length; i++) {
var url = getPostLink(post, i);
var rendered = getPostTitle(post, i);
var key = getPostImage(post, i);
var errors = getPostDate(post, i);
var delta = "";
switch(name) {
case "related":
delta = delta + ('<article class="related-item item-' + i + '"><div class="related-item-inner"><a class="post-image" href="' + url + '">' + key[0] + '</a><div class="post-header"><h2 class="post-title"><a href="' + url + '">' + rendered + '</a></h2><div class="post-meta">' + errors + "</div></div></div></article>");
break;
}
count_int = count_int + delta;
}
} else {
count_int = msgError();
}
count_int = count_int + "</div>";
table.html(count_int);
table.find("span.post-thumb").lazyify();
}
});
}
}
function ajaxRelated(table, value, key, text, all) {
if (all.match("getrelated")) {
return getAjax(table, value, key, text);
}
}
