var relatedUrls = new Array();
var thumburl = new Array();

function stripHtmlTags(s, max) {
    return s.replace(/<.*?>/ig, '').split(/\s+/).slice(0, max - 1).join(' ')
}

function createSummaryAndThumb(pID, title, url, date, comment, author, tag, authurl) {
    var posturl = url;
    var authorurl = authurl;
    var title = title;
    var date = date;
    var comment = comment;
    var tag = tag;
    var div = document.getElementById(pID);
    var img = div.getElementsByTagName("img");
    var content1 = div.innerHTML.replace(/<img.*?>/ig, '').replace(/<iframe.*?>/ig, '');
    var arr = content1.split(/<br\s*\/?>/);
    var content = arr[0] + arr.slice(1, -1).join('<br>') + arr.slice(-1);
    var month = new Array();
    month[0] = "Jan";
    month[1] = "Feb";
    month[2] = "Mar";
    month[3] = "Apr";
    month[4] = "May";
    month[5] = "Jun";
    month[6] = "Jul";
    month[7] = "Aug";
    month[8] = "Sep";
    month[9] = "Oct";
    month[10] = "Nov";
    month[11] = "Dec";
    var n = month[date.split('/')[0]];
    var date1 = date.split('/')[1];
    var year = date.split('/')[2];
    if (img.length == 1) {
        var imgurl = img[0].src;
        var thumb = '<div class="post-image"><a href="' + posturl + '"><img class="thumb" width="940" height="987" src="' + imgurl + '"></a></div>';
        var summary1 = thumb + '<div class="articlewrap"><header class="post-header">' + tag + '<h2 class="entry-title"><a href=' + posturl + '>' + title + '</a></h2></header><div class="entry-content clear"><div class="intro-text">' + stripHtmlTags(content, 50) + ' </div><div class="post-footer"><div class="cen"><div class="post-details"><span class="auth"> <i class="fa fa-user"></i><a href=' + authorurl + '>' + author + '</a></span> <span><i class="fa fa-newspaper-o"></i> <span class="eta"></span> Reading</span><span class="post-com"> <i class="fa fa-comments-o"></i>' + comment + ' Comments</span></div></div></div></div></div>'
    } else {
        if (img.length > 1) {
            var thum = '';
            for (var i = 0; i < img.length; i++) {
                thum = thum + '<a class="imagearray" href="' + posturl + '"><img src="' + img[i].src + '"></a>'
            }
            var thumb = '<div class="post-slider">' + thum + '</div>';
            var summary1 = thumb + '<div class="articlewrap"><header class="post-header">' + tag + '<h2 class="entry-title"><a href=' + posturl + '>' + title + '</a></h2></header><div class="entry-content clear"><div class="intro-text">' + stripHtmlTags(content, 50) + ' </div><div class="post-footer"><div class="cen"><div class="post-details"><span class="auth"> <i class="fa fa-user"></i><a href=' + authorurl + '>' + author + '</a></span><span><i class="fa fa-newspaper-o"></i> <span class="eta"></span> Reading</span><span class="post-com"> <i class="fa fa-comments-o"></i>' + comment + ' Comments</span></div></div></div></div></div>'
        } else {
            var frame = div.getElementsByTagName("iframe");
            if (frame.length >= 1) {
                var iframe1 = frame[0].src;
                var thumb = '<div class="post-image"><iframe width="100%" height="450" frameborder="no" src="' + iframe1 + '" scrolling="no"></iframe></div>';
                var summary1 = thumb + '<div class="articlewrap"><header class="post-header">' + tag + '<h2 class="entry-title"><a href=' + posturl + '>' + title + '</a></h2></header><div class="entry-content clear"><div class="intro-text">' + stripHtmlTags(content, 50) + ' </div><div class="post-footer"><div class="cen"><div class="post-details"><span class="auth"> <i class="fa fa-user"></i><a href=' + authorurl + '>' + author + '</a></span><span><i class="fa fa-newspaper-o"></i> <span class="eta"></span>Reading</span><span class="post-com">  <i class="fa fa-comments-o"></i>' + comment + ' Comments</span></div></div></div></div></div>'
            } else {
                var summary1 = '<div class="articlewrap"><header class="post-header">' + tag + '<h2 class="entry-title"><a href=' + posturl + '>' + title + '</a></h2></header><div class="entry-content clear"><div class="intro-text">' + stripHtmlTags(content, 50) + ' </div><div class="post-footer"><div class="cen"><div class="post-details"><span class="auth"> <i class="fa fa-user"></i><a href=' + authorurl + '>' + author + '</a></span><span><i class="fa fa-newspaper-o"></i> <span class="eta"></span> Reading</span><span class="post-com">  <i class="fa fa-comments-o"></i>' + comment + ' Comments</span></div></div></div></div></div>'
            }
        }
    }
    div.innerHTML = summary1;
    div.style.display = "block";
    var elem = document.getElementsByClassName("separator");
    for (var i = 0; i < elem.length; i++) {
        elem[i].innerHTML = ''
    }
}      //

head.script:
      //
var relatedTitles = new Array();
var relatedTitlesNum = 0;
var relatedUrls = new Array();
var thumburl = new Array();

function related_results_labels_thumbs(json) {
    for (var i = 0; i < json.feed.entry.length; i++) {
        var entry = json.feed.entry[i];
        relatedTitles[relatedTitlesNum] = entry.title.$t;
        try {
            thumburl[relatedTitlesNum] = entry.gform_foot.url
        } catch (error) {
            s = entry.content.$t;
            a = s.indexOf("<img");
            b = s.indexOf("src=\"", a);
            c = s.indexOf("\"", b + 5);
            d = s.substr(b + 5, c - b - 5);
            if ((a != -1) && (b != -1) && (c != -1) && (d != "")) {
                thumburl[relatedTitlesNum] = d
            } else thumburl[relatedTitlesNum] = 'https://3.bp.blogspot.com/-ltyYh4ysBHI/U04MKlHc6pI/AAAAAAAADQo/PFxXaGZu9PQ/w200-h140-c/no-image.png'
        }
        if (relatedTitles[relatedTitlesNum].length > 35) relatedTitles[relatedTitlesNum] = relatedTitles[relatedTitlesNum].substring(0, 35) + "...";
        for (var k = 0; k < entry.link.length; k++) {
            if (entry.link[k].rel == 'alternate') {
                relatedUrls[relatedTitlesNum] = entry.link[k].href;
                relatedTitlesNum++
            }
        }
    }
}

function removeRelatedDuplicates_thumbs() {
    var tmp = new Array(0);
    var tmp2 = new Array(0);
    var tmp3 = new Array(0);
    for (var i = 0; i < relatedUrls.length; i++) {
        if (!contains_thumbs(tmp, relatedUrls[i])) {
            tmp.length += 1;
            tmp[tmp.length - 1] = relatedUrls[i];
            tmp2.length += 1;
            tmp3.length += 1;
            tmp2[tmp2.length - 1] = relatedTitles[i];
            tmp3[tmp3.length - 1] = thumburl[i]
        }
    }
    relatedTitles = tmp2;
    relatedUrls = tmp;
    thumburl = tmp3
}

function contains_thumbs(a, e) {
    for (var j = 0; j < a.length; j++)
        if (a[j] == e) return true;
    return false
}

function printRelatedLabels_thumbs() {
    for (var i = 0; i < relatedUrls.length; i++) {
        if ((relatedUrls[i] == currentposturl) || (!(relatedTitles[i]))) {
            relatedUrls.splice(i, 1);
            relatedTitles.splice(i, 1);
            thumburl.splice(i, 1);
            i--
        }
    }
    var r = Math.floor((relatedTitles.length - 1) * Math.random());
    var i = 0;
    if (relatedTitles.length > 0) document.write('<h1>' + relatedpoststitle + '</h1>');
    document.write('<div class="xjxj" style="clear: both;"/>');
    while (i < relatedTitles.length && i < 20 && i < maxresults) {
        document.write('<a style="text-decoration:none;margin:0 2px 0px 0;float:left;');
        if (i != 0) document.write('"');
        else document.write('"');
        document.write(' href="' + relatedUrls[r] + '"><img class="related_img" src="' + thumburl[r] + '"/><br/><div class="related_title">' + relatedTitles[r] + '</div></a>');
        if (r < relatedTitles.length - 1) {
            r++
        } else {
            r = 0
        }
        i++
    }
    document.write('</div>');
    relatedUrls.splice(0, relatedUrls.length);
    thumburl.splice(0, thumburl.length);
    relatedTitles.splice(0, relatedTitles.length)
}      //

