jQuery(document).ready(function($) {
    "use strict";
    $('#top-search a').on('click', function(e) {
        e.preventDefault();
        $('.show-search').toggle('slide');
    });
   
 $(window).scroll(function() {
        if ($(this).scrollTop() > 200) {
            $('.to-top').fadeIn()
        } else {
            $('.to-top').fadeOut()
        }
    });


    $('.to-top').hide().click(function() {
        $('html, body').animate({
            scrollTop: 0
        }, 800);
        return false;
    });


    $('.bxslider').bxSlider({
        adaptiveHeight: true,
        mode: 'fade',
        pager: false,
        captions: true,
  nextText: '<i class="fa fa-angle-right"></i>',
